mapper.delete:

DELETE FROM REVIEW 
	         WHERE (USER_NO = #{userNo}
			  AND LOCATION_NO = #{locationNo})
			  AND REVIEW_NO = #{reviewNo}

mapper.update:
UPDATE OWNER_REPLY SET OWNER_REPLY_CONTENT = #{content},
 		ENROLL_DATE = CURRENT_DATE
		WHERE  REVIEW_NO = #{reviewNo}

UPDATE REVIEW SET
	USER_NO = #{userNo},
	MODIFY_DATE = DEFAULT,
	REVIEW_CONTENT = #{reviewContent}
	WHERE REVIEW_NO = #{reviewNo}
