mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE USER_ID = #{checkId}

SELECT COUNT(*) AS COUNT
FROM MEMBER
WHERE USER_PHONE = #{userPhone}

SELECT COUNT(*) AS COUNT
FROM CERTIFY_CODE
WHERE PHONE = #{phone}

mapper.update:
	UPDATE MEMBER
	   SET USER_PHONE = #{phoneNumber}
	 WHERE STATUS = true
 	   AND USER_NO = #{userNo}

UPDATE MEMBER
   SET USER_EMAIL = #{email}
 WHERE STATUS = true
   AND USER_NO = #{userNo}

UPDATE MEMBER
   SET USER_PWD = #{bossPwd}
 WHERE STATUS = true
   AND USER_NO = #{userNo}

	UPDATE MEMBER
   	   SET STATUS = false
     WHERE USER_ID = #{bossId}

UPDATE MEMBER
	SET STATUS = false
WHERE USER_NO = #{userNo}
